export default function CareerDetails() {
  const { id } = useParams()
  return(
    <div className="career-details">
      <h2>{ id}</h2>
    </div>
  )
}

// loader function
export const careerDetailsLoader = async ({params}) => {
  const { id } = params;

  const res = await fetch('http://localhost:4000/careers')
}
